function initialize(){var e={zoom:4,scaleControl:!0,center:centerOfUS},a={url:"assets/hiker-small.png"};map=new google.maps.Map(document.getElementById("main-map"),e);var t=new TrailCollection;t.fetch(function(){$.each(t.trails,function(e,t){var i=new google.maps.LatLng(t.startMarker[0],t.startMarker[1]);marker=new google.maps.Marker({map:map,icon:a,animation:google.maps.Animation.DROP,position:i});var n=new google.maps.InfoWindow({content:"<b>Title: </b>"+this.title+"<br/><b>State: </b>"+this.state+"<br/><b>Description: </b>"+this.description+'<br/><button class="favorite" data-id='+this.id+">Favorite This Hike</button>"});google.maps.event.addListener(marker,"click",function(){n.open(map,this,addFavorite),addFavorite()})})})}var centerOfUS=new google.maps.LatLng(39.8104,-96.556),marker,map;google.maps.event.addDomListener(window,"load",initialize);